import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme, GlobalStyles, Scrollbars } from './themes.js';
import axios from 'axios';
/* bodyBg: "snow",
headers: nearBlack,
topNavBg: darkGray,
topNavBgHover: medGray,
topNavTxt: nearWhite,
logoBannerBg: "linear-gradient(to right, #337BD2, #A6C9F3)",
userTxt: "snow",
activeCustTxt: "snow",
tableHeadBg: lightGray,
tableHeadTxt: nightBase0,
tableDataBg: "snow",
tableDataBgOdd: nearWhite,
tableDataTxt: nightBase0, */


const StyledApp = styled.div``
function ThemeChanger(props) {
    const [theme, setTheme] = useState('light');
    const themeToggler = () => {
        console.log("themeToggler ran");

        // GET THE CURRENT THEME
        let url = "/usersetting/9";
        axios.get(url).then(response => 
            global.Theme = response.data.theme
            // console.log("reponse data: " + response.data.theme)
        );

        // TOGGLE THE THEME
        global.Theme === 'light' ? global.Theme = 'dark' : global.Theme = 'light';
        
        // SET THEME TO UPDATED/TOGGLED
        global.Theme === 'light' ? setTheme('light') : setTheme('dark');

        // UPDATE THE TOGGLED THEME IN DB
        console.log("global.Theme: " +  global.Theme)
        setTimeout(() => {
            axios.put(url, {
                theme: global.Theme
            });
        },10)
    }
    return (
        <ThemeProvider theme={global.Theme === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            { theme === 'dark' ? <Scrollbars /> : null }
            <StyledApp>
                <div className="universalDark">
                    <div className="value">
                        <label className="switch">
                            <input id="darkMode" type="checkbox" onClick={themeToggler} />
                            <span className="slider round"></span>
                        </label>
                    </div>
                </div>
            </StyledApp>
        </ThemeProvider>
    )
}

export default ThemeChanger;
