import React from "react";
// import axios from "axios";

class Settings extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: "",
            pageTitle: "Settings",
        };
    }

    showSettings = (a) => {
        let inp = document.getElementById(a);
        console.log(inp);
        setTimeout(() => {
            if(this.props.userSettings[a] && inp.type === 'checkbox') {
                // console.log(this.props.userSettings.darkMode);
                inp.checked = true;
            }
            console.log("ran with value " + a)
        }, 100)
    }

    updateSettings = () => {
        let url = "/usersetting/" + this.props.userID;
        axios.put(url, { 
      //session_id: 666,
      // user: document.getElementById("username").value,
      // password: document.getElementById("password").value,
      logged_in: "false"
  },
/*         { params: {
      session_id: this.state.session_id
    }} */).then(response => {
    console.log("log out was called");
    window.location.pathname = "/login";
  })
    }

    componentDidMount = () => {
        this.showSettings("darkMode");
    }

    render() {
        return (
            <div className="component">
                <h3>Settings</h3>
                <div className="settingsContainer">

                    <div className="settingsBlock">

                        <div className="keyValueContainer">
                            <div className="key">
                                Dark Mode
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="darkMode" type="checkbox" onClick={this.updateSettings} />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
        );
    }
}

export default Settings;
