import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme, GlobalStyles } from './themes.js';
import Appy from "./Appy";

/* bodyBg: "snow",
headers: nearBlack,
topNavBg: darkGray,
topNavBgHover: medGray,
topNavTxt: nearWhite,
logoBannerBg: "linear-gradient(to right, #337BD2, #A6C9F3)",
userTxt: "snow",
activeCustTxt: "snow",
tableHeadBg: lightGray,
tableHeadTxt: nightBase0,
tableDataBg: "snow",
tableDataBgOdd: nearWhite,
tableDataTxt: nightBase0, */


const StyledApp = styled.div`
&::-webkit-scrollbar {
    width: 14px !important;
    color: ${props => props.theme.nightWhite};
}
&::-webkit-scrollbar-button { 
    background: ${props => props.theme.nightBase0}; 
    text: ${props => props.theme.nightWhite}; 
}

&::-webkit-scrollbar-thumb {
    background: ${props => props.theme.nightBase2};
    color: ${props => props.theme.nightWhite};
}

&::-webkit-scrollbar-track {
    background: ${props => props.theme.nightBase0};
    color: ${props => props.theme.nightWhite};
}

&::-webkit-scrollbar-track-piece {
    background: ${props => props.theme.nightBase0};
    color: ${props => props.theme.nightWhite};
}

&::-webkit-scrollbar-corner {
    background: ${props => props.theme.nightBase0};
    color: ${props => props.theme.nightWhite};
}
&::-webkit-resizer {
    background: ${props => props.theme.nightBase0};
    color: ${props => props.theme.nightWhite};
}    
&::-webkit-scrollbar-button:single-button:vertical:decrement {
    border-width: 0 8px 8px 8px;
    border-color: transparent transparent #555555 transparent;
}
&::-webkit-scrollbar-button:single-button:vertical:decrement:hover {
    border-color: transparent transparent #777777 transparent;
}
&::-webkit-scrollbar-button:single-button:vertical:increment {
    border-width: 8px 8px 0 8px;
    border-color: #555555 transparent transparent transparent;
}
&::-webkit-scrollbar-button:vertical:single-button:increment:hover {
    border-color: #777777 transparent transparent transparent;
}
&::-webkit-scrollbar {
    width: 16px;
    height: 16px;
}
&::-webkit-scrollbar-corner, ::-webkit-scrollbar-track {
    background-color: rgb(64, 64, 64);
}
&::-webkit-scrollbar-thumb {
    background-color: rgb(96, 96, 96);
    background-clip: padding-box;
    border: 2px solid transparent;
}
&::-webkit-scrollbar-thumb:hover {
    background-color: rgb(112, 112, 112);
}
&::-webkit-scrollbar-thumb:active {
    background-color: rgb(128, 128, 128);
}   
&::-webkit-scrollbar-button:single-button {
    background-color: ${props => props.theme.nightBase0};
    display: block;
    background-size: 10px;
    background-repeat: no-repeat;
}
.logoStrip {
    background: ${props => props.theme.logoStrip};
}

    .customer, .datagrid, .customer.datagrid, body, html {
        background: ${props => props.theme.nightBase35} !important;
        z-index: 10000 !important;
        position; relative;
        padding: 12px 11px 14px 9px;
        margin: 0 !important;
    }
    .logoStrip {
        background: ${props => props.theme.logoStrip};
    }
    #user,
    #settings {
        color: ${props => props.theme.userTxt};
    }
    .topnav, .topnav a {
        background-color: ${props => props.theme.nightBase0};
        color: ${props => props.theme.nightWhite2};
    }
    .topnav a:hover {
        background-color: ${props => props.theme.nightBase1};
        color: ${props => props.theme.nightWhite};
    }
    #activeCustomer {
        color: ${props => props.theme.nightBase3};
    }
    h3, h4, 
    .paginationContainer,
    .paginationContainer .paginate,
    .paginationContainer label,
    .customer table tbody tr th span i {
        color: ${props => props.theme.nightWhite2}
    }
    .paginateActive {
        background-color: ${props => props.theme.nightBase3};
        color: ${props => props.theme.nightWhite2};
    }
    .customer tr th,
    .customer tr th input,
    .grid,
    .customer,
    .datagrid,
    #table {
        background-color: ${props => props.theme.nightBase1} !important;
        color: ${props => props.theme.nightWhite2};
    }
    .customer tr th {
        outline: 1px solid ${props => props.theme.nightBase0};
    }
    .grid {
        border: 1px solid ${props => props.theme.nightBase0};
    }
    .grid table tr td {
        background-color: ${props => props.theme.nightBase1};
        outline: 1px solid ${props => props.theme.nightBase0};
    }
    .grid table tr:nth-child(odd) td {
        background-color: ${props => props.theme.nightBase2};
    }
    .customer input[type="checkbox"] {
        opacity: 0.8;
    }
    
`
function App() {
    const [theme, setTheme] = useState("dark");
    const themeToggler = () => {
        theme === 'light' ? setTheme('dark') : setTheme('light');
    }
    return (
        <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            <StyledApp>
                <div className="universalDark">
                    <div className="value">
                        <label className="switch">
                            <input id="darkMode" type="checkbox" onClick={themeToggler} />
                            <span className="slider round"></span>
                        </label>
                    </div>
                </div>
                <Appy />
            </StyledApp>
        </ThemeProvider>
    )
}

export default App;
