import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme, GlobalStyles } from './themes.js';
import Appy from "./Appy";

/* bodyBg: "snow",
headers: nearBlack,
topNavBg: darkGray,
topNavBgHover: medGray,
topNavTxt: nearWhite,
logoBannerBg: "linear-gradient(to right, #337BD2, #A6C9F3)",
userTxt: "snow",
activeCustTxt: "snow",
tableHeadBg: lightGray,
tableHeadTxt: nightBase0,
tableDataBg: "snow",
tableDataBgOdd: nearWhite,
tableDataTxt: nightBase0, */

const StyledApp = styled.div`
    body {
        background: ${props => props.theme.bodyBg};
    }
    .logoStrip {
        background: ${props => props.theme.logoBannerBg};
    }
    #user,
    #settings {
        color: ${props => props.theme.userText};
        color: ${props => props.theme.userTextHover};
    }
    .topnav, .topnav a {
        background-color: ${props => props.theme.topnavBackground};
        color: ${props => props.theme.userText};
    }
    .topnav a:hover {
        background-color: ${props => props.theme.topnavBackgroundHover};
    }
    .customer th,
    .customer th input {
        background-color: ${props => props.theme.tableHeadBg};
        color: ${props => props.theme.tableHeadTxt};
    }

`
function App() {
    const [theme, setTheme] = useState("light");
    const themeToggler = () => {
        theme === 'light' ? setTheme('dark') : setTheme('light');
    }
    return (
        <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            <StyledApp>
                <div className="universalDark">
                    <div className="value">
                        <label className="switch">
                            <input id="darkMode" type="checkbox" onClick={themeToggler} />
                            <span className="slider round"></span>
                        </label>
                    </div>
                </div>
                <Appy />
            </StyledApp>
        </ThemeProvider>
    )
}

export default App;
