import React from "react";
 import axios from "axios";

class Settings extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: "",
            pageTitle: "Settings",
        };
    }

    showSettings = (a) => {
        let inp = document.getElementById(a);
        // console.log(inp);
        setTimeout(() => {
            if(this.props.userSettings[a] === "true" && inp.type === 'checkbox') {
                // console.log(this.props.userSettings.darkMode);
                inp.checked = true;
            }
            // console.log("ran with value " + a)
        }, 1000)
    }

    updateSettings = (event) => {
        console.log(this.props.userID);
        let url = "/usersetting/" + this.props.userID;
        let meee = event.target.id;
        // console.log(meee);
        let newValue;
        if(this.props.userSettings[meee] === "true") {
            newValue = "false";
        } else {
            newValue = "true";
        }
        console.log(newValue);
        axios.put(url, { 
            darkMode: newValue
        });
            this.showSettings(meee);
            // console.log("ok it happened");
    }

    componentDidMount = () => {
        this.showSettings("darkMode");
    }

    render() {
        return (
            <div className="component">
                <h3>Settings</h3>
                <div className="settingsContainer">

                    <div className="settingsBlock">

                        <div className="keyValueContainer">
                            <div className="key">
                                Dark Mode
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="darkMode" type="checkbox" onClick={this.updateSettings} />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
        );
    }
}

export default Settings;
