import React from "react";
import axios from "axios";
import ThemeChanger from "../../ThemeChanger.js";

class Settings extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: "",
            pageTitle: "Settings",
            theme: "light",
        };
    }

    componentDidMount = () => {
        this.getSettings();
    }

    getSettings = () => {
        console.log(" GET SETTINGS !! ")
        axios.get("/usersetting/9").then(
            response => (global.theme = response.data.theme)
        ).then(
            setTimeout(() => {
                console.log("after a time: " + global.theme);
                // UPDATE DB & THEME HERE

                if (global.theme === "dark") {
                    let darkMode = document.getElementById("darkMode");
                    darkMode.checked = true;
                }
                
            }, 200)
        );
    }

    /*     showSettings = (a) => {
            let inp = document.getElementById(a);
            // console.log(inp);
            setTimeout(() => {
                if(this.props.userSettings[a] === "true" && inp.type === 'checkbox') {
                    // console.log(this.props.userSettings.darkMode);
                    inp.checked = true;
                }
                // console.log("ran with value " + a)
            }, 150)
        } */

    setThemeProp = (something) => {
        this.setState({
            theme: something
        }, function () {
            setTimeout(() => {
                console.log("setThemeProp was called");
                console.log(this.state.theme);
            }, 1000)
        }
        );
    }

    render() {
        return (
            <div className="component">
                <h3>Settings</h3>
                <div className="settingsContainer">

                    <div className="settingsBlock">

                        <div className="keyValueContainer">
                            <div className="key">
                                Dark Mode
                            </div>
                            <div className="value">
                                <ThemeChanger setThemeProp={this.setThemeProp} theme={this.state.theme} />
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Dark Mode
                            </div>
                            <div className="value">
                                <ThemeChanger />
                                {/*                                 <label className="switch">
                                    <input id="darkMode" type="checkbox" />
                                    <span className="slider round"></span>
                                </label> */}
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="nextSetting" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
        );
    }
}

export default Settings;
