import React from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import $ from 'jquery';
//import '../../App.css';

class TypeAhead extends React.Component {

        constructor(props) {
            super(props);
            this.state = {
                searchResults: [],
                downCount: -1
            };
        }
        componentDidMount() {
            console.log("typeAhead foot locker");
            document.getElementById(this.props.obj).addEventListener("focus", this.setTypeAheadParams);
        }

        setTypeAheadParams = () => {
            console.log("params set!");
            let url = "/params/666";
            axios.put(url, {
                typeAheadSelect: "title",
                typeAheadTable: this.props.table
            })
        }

        hideResults = (e) => {
            // let resultsContainer = document.getElementById(this.props.obj + "Results");
            if(e.target.className !== "TAResult") {
                // resultsContainer.className = "hidden";
                console.log("didn't match criteria, so hid it!");
            }
        }

        showResults = () => {
            let resultsContainer = document.getElementById(this.props.obj + "Results");
            resultsContainer.className = "TAResults";
        }

        getSearch = () => {
            let inp = document.getElementById(this.props.obj);
            let idStr = this.props.obj + "Results";
            let results = document.getElementById(idStr);
            // console.log(results);
            // console.log(this.props.obj + "Results");
            if(inp.value === "") {
                results.className = "hidden";
            } 
            // console.log("search for " + inp.value);
            let url = "/params/666";
            axios.put(url, {
                typeAheadInput: inp.value
            })
            let wiegehts = "/typeahead"

            clearTimeout(global.searchDelay);
            global.searchDelay = setTimeout(() => {
                if(inp.value !== "") {
                    axios.get(wiegehts).then(response => {
                        this.setState({
                            searchResults: response.data,
                            downCount: -1
                        }, function() {
                            //setTimeout(() => {
                                if(inp.value !== "") {
                                    results.className = "TAResults";
                                } 
                            //},200)
                            // console.log(response.data);
                            // console.log("wiegehts!");
                        })
                    })
                }
              // 0.2 seconds
            }, 200);
        }

        enter = (e) => {
            this.searchInput.current.value = e.target.innerHTML;
            this.customerid.current.value = e.target.id;
        };

        searchResultsResetClasses = () => {
            let resultsContainer = document.getElementById(this.props.obj + "Results");
            for(var i = 0; i < resultsContainer.childNodes.length; i++) {
                resultsContainer.childNodes[i].className = "TAResult";           
            }
        }

        mouseSetDown = (e) => {
            let resultsContainer = document.getElementById(this.props.obj + "Results");
            let resultsArr = [];
            for (var i = 0; i < resultsContainer.childNodes.length; i++) {
                resultsArr.push(resultsContainer.childNodes[i].id);
            }
            this.searchResultsResetClasses();
            this.setState({
                downCount: resultsArr.indexOf(e.target.id)
            })  
            console.log(resultsArr.indexOf(e.target.id));
            global.mouseOver = resultsArr.indexOf(e.target.id);
        }

        checkKey = (e) => {

            $('#' + this.props.obj).bind('keydown', function(e){
                if(e.keyCode === 38 || e.keyCode === 40){
                    e.preventDefault();
                }
            });
            let resultsContainer = document.getElementById(this.props.obj + "Results");
            var limitHigh = resultsContainer.childElementCount;
            var limitLow = 0;
            var myCount = this.state.downCount;
            if(myCount < limitLow) {
                myCount = -1;
            } 
            if(myCount >= limitHigh) {
                myCount = limitHigh;
            } 
            // 65-90 are a-z ;; space is 32 ;; 48-57 are numbers ;;  189 is hypen
            if(myCount <= limitHigh - 1 && myCount >= limitLow && limitHigh > 0) {
                resultsContainer.childNodes[myCount].className = "TAResultActive";           
            }
            if (e.keyCode === 40) {
                // down arrow
                global.mouseOver++;
                resultsContainer.childNodes[global.mouseOver].className = "TAResultD";

                myCount = myCount + 1;
                if(myCount <= limitLow) {
                    myCount = 0;
                } 
                if(myCount >= limitHigh - 1) {
                    myCount = limitHigh - 1;
                } 
                if(myCount > limitLow) {
                    resultsContainer.childNodes[myCount - 1].className = "TAResult";           
                }
                if(myCount < limitHigh - 1) {
                    resultsContainer.childNodes[myCount + 1].className = "TAResult";           
                }
                if(myCount <= limitHigh && myCount >= limitLow && limitHigh > 0) {
                    resultsContainer.childNodes[myCount].className = "TAResultActive";           
                }       
                this.setState({
                    downCount: myCount
                })
            } else if (e.keyCode === 38) {
                // up arrow
                myCount = myCount - 1;
                if(myCount <= limitLow) {
                    myCount = 0;
                } 
                if(myCount >= limitHigh) {
                    myCount = limitHigh;
                } 
                if(myCount !== limitLow) {
                    resultsContainer.childNodes[myCount - 1].className = "TAResult";           
                }
                if(myCount < limitHigh - 1) {
                    resultsContainer.childNodes[myCount + 1].className = "TAResult";           
                }
                if(myCount <= limitHigh && myCount >= limitLow && limitHigh > 0) {
                    resultsContainer.childNodes[myCount].className = "TAResultActive";           
                }
                this.setState({
                    downCount: myCount
                })       
            } else if (e.keyCode === 13) {
                // enter
                if(myCount < limitLow) {
                    myCount = 0;
                } 
                if(myCount >= limitHigh) {
                    myCount = limitHigh - 1;
                } 
                resultsContainer.childNodes[myCount].click();           
            }
        }

        render() {
                if (this.props.location.pathname === '/login') return null;
                return ( 
                <div className="typeAheadContainer">
                    <input onKeyDown={this.checkKey} onChange={this.getSearch} onBlur={this.hideResults} onFocus={this.showResults} className="typeAheadDarkTest" id={this.props.obj} type="text" />
                    <div id={this.props.obj + "Results"} onMouseOver={this.searchResultsResetClasses} className="TAResultsContainer">
                        {this.state.searchResults.map(p => (
                            <div onMouseOver={this.mouseSetDown} className="TAResult" id={p.id} key={p.id}>
                                <span className="primary">{p.returnedData}</span>
                                <span className="secondary">${p.secondary}</span>
                            </div>
                        ))
                        }
                    </div>
                </div>
      );
    }
  }

  export default withRouter(TypeAhead);
