import React from "react";
// import axios from "axios";

class Settings extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: "",
            pageTitle: "Settings",
        };
    }

    showSettings = (a) => {
        let inp = document.getElementById(a);
        console.log(inp);
        setTimeout(() => {
            if(this.props.userSettings.darkMode) {
                console.log(this.props.userSettings.darkMode);
                inp.checked = true;
            }
            console.log("ran with value " + a)
        }, 600)
    }

    componentDidMount = () => {
        this.showSettings("darkMode");
    }

    render() {
        return (
            <div className="component">
                <h3>Settings</h3>
                <div className="settingsContainer">

                    <div className="settingsBlock">

                        <div className="keyValueContainer">
                            <div className="key">
                                Dark Mode
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input id="darkMode" type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                        <div className="keyValueContainer">
                            <div className="key">
                                Next Setting
                            </div>
                            <div className="value">
                                <label className="switch">
                                    <input type="checkbox" />
                                    <span className="slider round"></span>
                                </label>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
        );
    }
}

export default Settings;
