import { createGlobalStyle } from 'styled-components'; 

let blue = "#297CE0";
let lightBlue = "#95CFF2";
let darkGreen = "#69E26A";
let lightGreen = "#69E26A";
let medGreen = "#69E26A";
let nearBlack = "#1F1F1F";
let darkGray = "#505050";
let medGray = "#A6A6A6";
let lightGray = "#CCCCCC";
let nearWhite = "#ECECEC";
let lightYellow = "#fffccf";
let verizonRed = "#D52B1E";
let myRed = "#ED2323";

// DARK MODE
let nightBlack = "#1c1c1c";
let nightBase0 = "#000000";
let nightBase1 = "#171717";
let nightBase2 = "#1b1b1b";
let nightBase3 = "#363636";
let nightBase4 = "#565656";
let nightYellow = "#8A8700";
let nightRed = "#7F0000";
let nightGreen = "#126200";
let nightPurple = "#350074";
let nightBlue = "#092D54";
let nightBlue2 = "#1D508F";
let nightPink = "#740046";
let nightWhite = "#EBEBEB";
let nightWhite2 = "#0f0f0f";

export const lightTheme = {
    bodyBg: "snow",
    headers: nearBlack,
    topNavBg: nightBase3,
    topNavBgHover: nightBase4,
    topNavTxt: nearWhite,
    logoBannerBg: "linear-gradient(to right, #337BD2, #A6C9F3)",
    userTxt: "snow",
    userTxtHover: "snow",
    activeCustTxt: "snow",
    tableHeadBg: nearWhite,
    tableHeadTxt: nightBase0,
    tableDataBg: "snow",
    tableDataBgOdd: nearWhite,
    tableDataTxt: nightBase0,
}

export const darkTheme = {
    mainText: "#EBEBEB",
    logoStrip: "linear-gradient(to right, #092D54, #1D508F)",
    nightblack: "#1c1c1c",
    black0: "#000000",
    black05: "#171717",
    black1: "#1b1b1b",
    black2: "#363636",
    black3: "#565656",
    nightyellow: "#8A8700",
    nightred: "#7F0000",
    nightgreen: "#126200",
    nightpurple: "#350074",
    nightblue: "#092D54",
    nightblue2: "#1D508F",
    nightpink: "#740046",
    nightwhite: "#EBEBEB",
    nightwhite2: "#0f0f0f",
    userText: "#EBEBEB",
    topnavBackground: "#000000",
    topnavBackgroundHover: "#1b1b1b",
    userTextHover: "snow",
    gridHeaderBackground: "#363636",
}

export const GlobalStyles = createGlobalStyle`

    body {
        background-color: ${props => props.theme.body};
    }

`;
