import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme, GlobalStyles, Scrollbars } from './themes.js';
import axios from 'axios';
/* bodyBg: "snow",
headers: nearBlack,
topNavBg: darkGray,
topNavBgHover: medGray,
topNavTxt: nearWhite,
logoBannerBg: "linear-gradient(to right, #337BD2, #A6C9F3)",
userTxt: "snow",
activeCustTxt: "snow",
tableHeadBg: lightGray,
tableHeadTxt: nightBase0,
tableDataBg: "snow",
tableDataBgOdd: nearWhite,
tableDataTxt: nightBase0, */


const StyledApp = styled.div``
function ThemeChanger(props) {
    const [theme, setTheme] = useState('light');
/*     const themeToggler = () => {
        console.log("this is bullshit");
        axios.get("/usersetting/9").then(
            response => (global.fuckery = response.data.theme)
        ).then(
            setTimeout(() => {
                console.log("global fuckery: " + global.fuckery)
                global.fuckery === 'light' ? global.fuckeryToggled = 'dark' : global.fuckeryToggled = 'light';
                console.log("global.fuckeryToggled: " + global.fuckeryToggled);

                // UPDATE DB & THEME HERE

                global.fuckeryToggled === "light" ? setTheme('light') : setTheme('dark');
                axios.put("/usersetting/9", {
                    theme: global.fuckeryToggled
                });

                // TOGGLE GLOBAL VARIABLE
                global.fuckery === 'light' ? global.fuckery = 'dark' : global.fuckery = 'light';
            },200)
        );
    } */
    return (
        <ThemeProvider theme={global.fuckeryToggled === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            { theme === 'dark' ? <Scrollbars /> : null }
            <StyledApp>
                <div className="universalDark">
                    <div className="value">
                        <label className="switch">
                            <input id="darkMode" type="checkbox" />
                            <span className="slider round"></span>
                        </label>
                    </div>
                </div>
            </StyledApp>
        </ThemeProvider>
    )
}

export default ThemeChanger;
