import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme, GlobalStyles } from './themes.js';
import Appy from "./Appy";

/* bodyBg: "snow",
headers: nearBlack,
topNavBg: darkGray,
topNavBgHover: medGray,
topNavTxt: nearWhite,
logoBannerBg: "linear-gradient(to right, #337BD2, #A6C9F3)",
userTxt: "snow",
activeCustTxt: "snow",
tableHeadBg: lightGray,
tableHeadTxt: nightBase0,
tableDataBg: "snow",
tableDataBgOdd: nearWhite,
tableDataTxt: nightBase0, */

const StyledApp = styled.div`
    .customer, .datagrid, .customer.datagrid, body, html {
        background: ${props => props.theme.nightBase35} !important;
        z-index: 10000 !important;
        position; relative;
        padding: 10px 11px 16px 9px;
        margin: 0 !important;
    }
    .logoStrip {
        background: ${props => props.theme.logoStrip};
    }
    #user,
    #settings {
        color: ${props => props.theme.userTxt};
    }
    .topnav, .topnav a {
        background-color: ${props => props.theme.nightBase0};
        color: ${props => props.theme.nightWhite2};
    }
    .topnav a:hover {
        background-color: ${props => props.theme.nightBase1};
        color: ${props => props.theme.nightWhite};
    }
    #activeCustomer {
        color: ${props => props.theme.nightBase3};
    }
    h3, h4, 
    .paginationContainer,
    .paginationContainer .paginate,
    .paginationContainer label,
    .customer table tbody tr th span i {
        color: ${props => props.theme.nightWhite2}
    }
    .paginateActive {
        background-color: ${props => props.theme.nightBase3};
        color: ${props => props.theme.nightWhite2};
    }
    .customer tr th,
    .customer tr th input,
    .grid,
    .customer,
    .datagrid,
    #table {
        background-color: ${props => props.theme.nightBase1} !important;
        color: ${props => props.theme.nightWhite2};
    }
    .customer tr th {
        outline: 1px solid ${props => props.theme.nightBase0};
    }
    .grid {
        border: 1px solid  ${props => props.theme.nightBase0};
    }

`
function App() {
    const [theme, setTheme] = useState("dark");
    const themeToggler = () => {
        theme === 'light' ? setTheme('dark') : setTheme('light');
    }
    return (
        <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            <StyledApp>
                <div className="universalDark">
                    <div className="value">
                        <label className="switch">
                            <input id="darkMode" type="checkbox" onClick={themeToggler} />
                            <span className="slider round"></span>
                        </label>
                    </div>
                </div>
                <Appy />
            </StyledApp>
        </ThemeProvider>
    )
}

export default App;
