import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme, GlobalStyles } from './themes.js';
import Appy from "./Appy";

const StyledApp = styled.div`

    color: ${props => props.theme.fontColor};

    .logoStrip {
        background: ${props => props.theme.logoStrip};
    }
    body, body *, 
    .orders th, .orders td, 
    h3, h4, 
    .grid table tr td, 
    .customer th, 
    .field input, .field select, 
    .field label, 
    .grid table tr:nth-child(2) th i, 
    .grid table tr:nth-child(2) th input, 
    .customerSearchResultActive, 
    .topnav button, 
    .topnav a:hover, .dropdown:hover .dropbtn, 
    .numberPerPageContainer, 
    #paginateBackArrow, 
    #newOrderContainer h3, 
    .logoBanner, 
    .quoterContainer h4, 
    .quoterTable table td, 
    .quoterTable input, 
    .addRow, 
    .tdTotals, 
    ::-webkit-scrollbar-button, 
    ::-webkit-scrollbar-track, 
    ::-webkit-scrollbar-track-piece, 
    ::-webkit-scrollbar-corner, 
    ::-webkit-resizer, 
    input:checked+.slider:after {
        color: ${props => props.theme.mainTextColor};
    }
    .customer td, 
    #paginateBackArrow:hover, #paginateForwardArrow:hover, 
    .paginateActive, 
    .logoBanner, 
    .quoterContainer h4, 
    .quoterTable table td, 
    .addRow, 
    ::-webkit-scrollbar-button:single-button:vertical:decrement {
        background-color: ${props => props.theme.mainBackgroundColor};
    }
    .currencyField, 
    #emptyCart, 
    .productListedInfoRightSection, 
    .productListedQuantityIncrement, 
    .productListedQuantity, 
    .productListedPrice, 
    .productListedlineItemTotal::before, 
    .modalHeader .modalHeaderText {
        color: ${props => props.theme.secondaryTextColor};
    }
    .cancel, 
    .saveOrder, 
    .newSave, 
    .back svg, 
    .addProductButton, 
    .logoLogin, 
    .loginButton:active, 
    .activeCustomer:hover {
        color: ${props => props.theme.tertiaryTextColor};
    }
    body, 
    .field input, .field select, 
    .grid table tr:nth-child(2) th, 
    .searchResults, 
    .customerSearchResult:hover, .customerSearchResult:active, 
    .topnav a:hover, .dropdown:hover .dropbtn, 
    .quoterTable input {
        background-color: ${props => props.theme.secondaryBackgroundColor};
    }
    .grid, 
    .productListedImage img, 
    #notes, 
    .productSearchResultsTile, 
    .returnedProduct.row:first-child, 
    .modalBody .quantityContainer, 
    .monthlyInner {
    	border: 1px solid ${props => props.theme.mainBorder};
    }
    #table tbody tr td,
    #table tbody tr th {
        outline: 1px solid ${props => props.theme.mainBorder};
    }
    .App-header, 
    .grid, 
    .backgroundAnimated, 
    .topnav a, 
    ::-webkit-scrollbar-button:single-button,
    .topnav {
    	background-color: ${props => props.theme.background4};
    }
    .gridToolbox [data-tooltipped], 
    .addRow:hover, 
    #grid-refresh:hover, 
    #grid-new:hover, 
    .slider:before {
	    background-color: ${props => props.theme.background5};
    }
`
function App() {
    const [theme, setTheme] = useState("light");
    const themeToggler = () => {
        theme === 'light' ? setTheme('dark') : setTheme('light');
    }
    return (
        <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            <StyledApp>
                <div className="universalDark">
                    <div className="value">
                        <label className="switch">
                            <input id="darkMode" type="checkbox" onClick={themeToggler} />
                            <span className="slider round"></span>
                        </label>
                    </div>
                </div>
                <Appy />
            </StyledApp>
        </ThemeProvider>
    )
}

export default App;
