/* FANCY CHECKBOX */

.cbx {
    -webkit-perspective: 20;
    perspective: 20;
    position: absolute;
    top: 50%;
    left: 22px;
    margin: -12px;
    border: 2px solid #e8e8eb;
    background: #e8e8eb;
    border-radius: 4px;
    transform: translate3d(0, 0, 0);
    cursor: pointer;
    transition: all 0.3s ease;
}

.cbx:hover {
    border-color: #0b76ef;
}

.flip {
    display: block;
    transition: all 0.4s ease;
    transform-style: preserve-3d;
    position: relative;
    width: 20px;
    height: 20px;
}

#cbx, #surchargeInput, #surchargeUniqueInput, #outOfStockInput, #trackStockInput {
    display: none;
}

#cbx:checked+.cbx, #surchargeInput:checked+.cbx, #surchargeUniqueInput:checked+.cbx, #outOfStockInput:checked+.cbx, #trackStockInput:checked+.cbx {
    border-color: #0b76ef;
}

#cbx:checked+.cbx .flip, #surchargeInput:checked+.cbx .flip, #surchargeUniqueInput:checked+.cbx .flip, #outOfStockInput:checked+.cbx .flip, #trackStockInput:checked+.cbx .flip {
    transform: rotateY(180deg);
}

/* generic input for checkbox */

.fancy_check {
    display: none;
}

.fancy_check:checked+.cbx {
    border-color: #0b76ef;
}

.fancy_check:checked+.cbx .flip {
    transform: rotateY(180deg);
}

/* END generic input for checkbox */

.front, .back {
    backface-visibility: hidden;
