/* The switch - the box around the slider */

.switch {
    position: relative;
    bottom: 2px;
    display: inline-block;
    width: 60px;
    height: 34px;
}

/* Hide default HTML checkbox */

.switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

/* The slider */

.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: var(--black-2);
    -webkit-transition: .2s;
    transition: .2s;
}

.slider:before {
    position: absolute;
    content: "";
    height: 30px;
    width: 30px;
    left: 2px;
    bottom: 2px;
    background-color: var(--black-3);
    transition: .2s;
}

input:checked+.slider {
    background-color: var(--night-blue2);
}

input:checked+.slider:after {
    font-family: sans-serif;
    content: 'ON';
    position: relative;
    left: 0;
    top: 0;
    color: var(--night-white);
    font-weight: 600;
    animation: fadeIn ease 1.4s;
    height: 34px;
    width: 43px;
    display: flex;
    justify-content: flex-end;
    padding: 0 9px;
    align-items: center;
    font-size: 12px;
}

input:checked+.slider:before {
    -webkit-transform: translateX(26px);
    -ms-transform: translateX(26px);
    transform: translateX(26px);
}

/* Rounded sliders */

.slider.round {
    border-radius: 34px;
}

.slider.round:before {
    border-radius: 50%;
}

.settingsBlock .keyValueContainer {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 0 25px;
}

.settingsBlock {
    /* border: 2px solid blue; */
    padding: 5px;
    max-width: 300px;
}

.settingsBlock .keyValueContainer .key {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    font-size: 18px;
    font-weight: 400;
  }

  &::-webkit-scrollbar-button:single-button:vertical:decrement {
    height: 12px;
    width: 16px;
    background-position: center 4px;
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonFill}'><polygon points='50,00 0,50 100,50'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:vertical:decrement:hover {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonHover}'><polygon points='50,00 0,50 100,50'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:vertical:decrement:active {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonActive}'><polygon points='50,00 0,50 100,50'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:vertical:increment {
    height: 12px;
    width: 16px;
    background-position: center 2px;
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonFill}'><polygon points='0,0 100,0 50,50'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:vertical:increment:hover {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonHover}'><polygon points='0,0 100,0 50,50'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:vertical:increment:active {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonActive}'><polygon points='0,0 100,0 50,50'/></svg>");
  }      
  &::-webkit-scrollbar-button:single-button:horizontal:decrement {
    height: 12px;
    width: 12px;
    background-position: 3px 3px;
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonFill}'><polygon points='0,50 50,100 50,0'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:horizontal:decrement:hover {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonHover}'><polygon points='0,50 50,100 50,0'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:horizontal:decrement:active {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonActive}'><polygon points='0,50 50,100 50,0'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:horizontal:increment {
