    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='${props => props.theme.scrollbarButtonFill}'><polygon points='0,0 0,100 50,50'/></svg>");
  }
  &::-webkit-scrollbar-button:single-button:horizontal:increment:hover {
