import { createGlobalStyle } from 'styled-components'; 

let blue = "#297CE0";
let lightBlue = "#95CFF2";
let darkGreen = "#69E26A";
let lightGreen = "#69E26A";
let medGreen = "#69E26A";
let nearBlack = "#1F1F1F";
let darkGray = "#505050";
let medGray = "#A6A6A6";
let lightGray = "#CCCCCC";
let nearWhite = "#ECECEC";
let lightYellow = "#fffccf";
let verizonRed = "#D52B1E";
let myRed = "#ED2323";
// DARK MODE
let nightBlack = "#1c1c1c";
let nightBase0 = "#000000";
let nightBase1 = "#171717";
let nightBase2 = "#1b1b1b";
let nightBase3 = "#363636";
let nightBase4 = "#565656";
let nightYellow = "#8A8700";
let nightRed = "#7F0000";
let nightGreen = "#126200";
let nightPurple = "#350074";
let nightBlue = "#092D54";
let nightBlue2 = "#1D508F";
let nightPink = "#740046";
let nightWhite = "#EBEBEB";
let nightWhite2 = "#E0E0E0";
let nightBase25 = "#2A2A2A";


export const lightTheme = {
/*     opacity: "1",
    mainText: nearBlack,
    headers: "#1F1F1F", */
}


export const darkTheme = {
    headers: "#EBEBEB",
    mainText: "#EBEBEB",
    logoStrip: "linear-gradient(to right, #092D54, #1D508F)",
    nightBlack: "#1c1c1c",
    nightBase0: "#000000",
    nightBase1: "#171717",
    nightBase2: "#1b1b1b",
    nightBase25: "#2A2A2A",
    nightBase3: "#363636",
    nightBase35: "#232323",
    nightBase4: "#565656",
    nightYellow: "#8A8700",
    nightRed: "#7F0000",
    nightGreen: "#126200",
    nightPurple: "#350074",
    nightBlue: "#092D54",
    nightBlue2: "#1D508F",
    nightPink: "#740046",
    nightWhite: "#EBEBEB",
    nightWhite2: "#E0E0E0",
    opacity: "0.6",
    // scrollbars ...
    scrollbarTrack: "#171717",
    scrollbarThumb: "#2A2A2A",
    scrollbarThumbOutline: "transparent",
    scrollbarThumbHover: nightBase25,
    scrollbarThumbActive: "gray",
    scrollbarButtonBackground: "#171717",
    scrollbarButtonFill: "gray",
    scrollbarButtonHover: "gray",
    scrollbarButtonActive:"gray"
}

export const Scrollbars = createGlobalStyle`
/* edits unique to dark mode
    -- checkbox type inputs opacity
    -- scroll bars are custom; may not use color variables
    -- need to edit which "background" images are referenced for light mode
*/

:root {
    --blue: #297CE0;
    --light-blue: #95CFF2;
    --dark-green: #69E26A;
    --light-green: #69E26A;
    --med-green: #69E26A;
    --near-black: #1F1F1F;
    --dark-gray: #505050;
    --med-gray: #A6A6A6;
    --light-gray: #CCCCCC;
    --near-white: #ECECEC;
    --light-yellow: #fffccf;
    --verizon-red: #D52B1E;
    --my-red: #ED2323;
    /* dark mode */
    --night-black: #1c1c1c;
    --black-0: #000000;
    --black-05: #171717;
    --black-1: #1b1b1b;
    --black-2: #363636;
    --black-3: #565656;
    --night-yellow: #8A8700;
    --night-red: #7F0000;
    --night-green: #126200;
    --night-purple: #350074;
    /* --night-blue: #002D74; */
    --night-blue: #092D54;
    --night-blue2: #1D508F;
    --night-pink: #740046;
    --night-white: #EBEBEB;
    --night-white2: #0f0f0f;
    /* icon colors */
    /*
    white is white
    dark gray: 505050
    light gray: CCCCCC
    night-white: ECECEC
    blue is 297CE0
    night-blue is 002D74
    green is 69E26A
    night-green: 126200
    red: ED2323
    night-red: 7F0000
    */
}

body {
    background-color: var(--black-1);
}

body, body * {
    color: var(--night-white);
}

/* BLUE: #1A73FF */

#root {
    max-width: 100vw !important;
    width: 100%;
    margin: 0 !important;
    padding: 0 !important;
    overflow-x: hidden;
}

.App-logo {
    animation: App-logo-spin infinite 20s linear;
    height: 40vmin;
    pointer-events: none;
}

.App-header {
    background-color: var(--black-0);
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: calc(10px + 2vmin);
    color: white;
}

.header {
    display: block;
}

.App-link {
    color: var(--light-blue);
}

@keyframes App-logo-spin {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}

.logoStrip {
    text-align: left;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    height: 58px;
    font-size: 2em;
    background: linear-gradient(to right, var(--night-blue), var(--night-blue2));
    border-bottom: 2px solid #616161;
    -webkit-touch-callout: none;
    /* iOS Safari */
    -webkit-user-select: none;
    /* Safari */
    -khtml-user-select: none;
    /* Konqueror HTML */
    -moz-user-select: none;
    /* Old versions of Firefox */
    -ms-user-select: none;
    /* Internet Explorer/Edge */
    user-select: none;
    /* Non-prefixed version, currently
    supported by Chrome, Opera and Firefox */
}

.logoStrip, .topnav {
    max-width: 100vw !important;
}

.topnav {
    display: flex;
    justify-content: space-between;
}

.navLink {
    float: left;
    margin: 10px 0 10px 20px;
    color: var(--med-gray);
    transition: 0.3s ease-in-out;
    text-decoration: none;
}

.navLink:hover {
    color: black;
    border-bottom: 1px solid black;
    transition: 0.3s ease-in-out;
}

.navLink2 {
    float: right;
    margin: 10px 20px;
    color: var(--med-gray);
    transition: 0.3s ease-in-out;
    text-decoration: none;
}

.navLink2:hover {
    color: black;
    border-bottom: 1px solid black;
    transition: 0.3s ease-in-out;
}

.ordersContainer {
    display: block;
    margin-top: 50px;
}

tr {
    max-height: 30px;
    height: 30px;
}

.orders {
    /* table-layout: auto; */
    margin: 0 25px;
    border-collapse: collapse;
}

.orders th, .orders td {
    border: 2px solid var(--black-0);
    color: var(--night-white);
    padding: 5px 10px;
    overflow: hidden;
    white-space: nowrap;
}

td, th {
    white-space: nowrap;
}

.ordersGrid {
    display: block;
}

.newCustomerContainer {
    display: block;
    margin-top: 50px;
    margin-left: 25px;
    text-align: left;
}

.newCustomerContainer input {
    display: block;
}

.ampersand {
    display: none;
    font-size: 26px;
    position: relative;
    bottom: 2px;
    margin-left: 4px;
    margin-right: 2px;
}

.words {
    position: relative;
    top: 4px;
    left: 60px;
    color: var(--med-gray);
    font-weight: 600;
}

.greenBoy {
    font-weight: 400;
    margin-right: 30px;
}

.logoSmall {
    position: absolute;
    left: 15px;
    top: 5px;
    height: 45px;
}

.logoWords {
    font-family: 'Exo 2', sans-serif;
    position: absolute;
    left: 51px;
    top: 1px;
    font-size: 43px;
    color: snow;
    text-shadow: 1px 1px 1px red;
}

.crmWords {
    font-size: 20px;
    position: relative;
    bottom: 5px;
}

body {
    margin: 0;
    font-family: Arial, Helvetica, sans-serif;
    -webkit-font-smoothing: antialiased;
}

.customer {
    position: relative;
    margin: 9px auto;
    padding: 0 10px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    max-width: 100%;
    max-height: calc(100vh - 135px) !important;
}

.customer h3 {
    font-size: 1.5em;
    margin: 3px 0 5px 3px;
    z-index: 1;
}

h3, h4 {
    color: var(--night-white);
}

.customer button {
    margin: 20px 10px;
    font-size: 15px;
    font-weight: 600;
    line-height: 30px;
    padding: 5px 15px;
    cursor: pointer;
}

.newOrderContainer button {
    margin: 20px 10px;
    font-size: 15px;
    font-weight: 600;
    line-height: 30px;
    padding: 5px 15px;
    cursor: pointer;
}

.green {
    background-color: var(--night-green);
}

.red {
    background-color: var(--night-red);
}

.cancel {
    color: snow;
    position: absolute;
    right: 150px;
    bottom: 10px;
    height: 48px;
    transition: 0.3s;
    font-size: 15px;
    font-weight: 600;
    line-height: 30px;
    padding: 5px 15px;
    cursor: pointer;
}

.saveOrder {
    color: snow;
    position: absolute;
    right: 10px;
    bottom: 10px;
    height: 48px;
    transition: 0.3s;
    font-size: 15px;
    font-weight: 600;
    line-height: 30px;
    padding: 5px 15px;
    cursor: pointer;
}

.saveOrder:hover {
    background-color: var(--night-green);
}

.newSave {
    background-color: var(--night-green);
    color: snow;
    /* -webkit-backface-visibility: hidden;
    -webkit-transform: translate3D(0, 0, 0); */
    transition: background-color 0.3s;
    transition: opacity 1.3s;
}

.newSave:hover {
    background-color: var(--night-green);
    /*   -webkit-backface-visibility: hidden;
    -webkit-transform: translate3D(0, 0, 0);
    transition: opacity 0.3s !important; */
}

.customer button:active {
    outline: none;
}

.grid table tr td {
    position: relative;
    outline: 1px solid var(--black-0);
    color: var(--night-white);
    border: none !important;
    background-color: var(--black-1);
    /* transition: 0.2s; */
}

.customer table {
    border-collapse: collapse;
}

.customer th {
    background-color: var(--black-05);
    color: var(--night-white);
    border: var(--black-0);
    overflow: visible !important;
    font-size: 13px;
}

.customer td {
    padding: 10px;
    font-size: 13px;
}

.customer td {
    background-color: var(--black-2);
}

/* BEGIN FANCY INPUTS */

body {
    font-family: Avenir Next, Avenir, SegoeUI, sans-serif;
}

form {
    margin: 0;
}

.field, .checkboxField {
    font-size: 16px;
}

.field {
    display: flex;
    flex-flow: column-reverse;
    padding: 0 10px 10px 10px;
    min-width: calc(50% - 20px);
    position: relative;
    top: 5px;
    margin-bottom: 3px;
    /* border: 1px solid red; */
}

.checkboxField {
    display: flex;
    align-items: center;
    position: relative;
    padding: 2px 10px 1px 42px;
    width: auto;
    min-height: 47px;
}

input:disabled {
    color: var(--med-gray);
    font-style: italic;
    cursor: not-allowed;
}

.disabledField {
    min-width: calc(50%) !important;
}

.checkboxFieldLabel {
    font-size: 16px !important;
}

.subForm {
    width: 100%;
}

.subForm .field {
    width: calc(100% - 20px);
}

.fieldFull {
    width: 100%;
    display: flex;
    justify-content: center;
}

.fieldFull textarea {
    width: calc(100% - 42px);
    height: 50px;
}

/**
  * Add a transition to the label and input.
  * I'm not even sure that touch-action: manipulation works on
  * inputs, but hey, it's new and cool and could remove the 
  * pesky delay.
  */

.field label, .field input, .field select {
    transition: all 0.2s;
    touch-action: manipulation;
}

.field input, .field select {
    font-size: 1em;
    border: 0;
    border-bottom: 1px solid var(--night-white);
    font-family: inherit;
    -webkit-appearance: none;
    border-radius: 0;
    padding: 0;
    cursor: text;
    margin-bottom: 1px;
    background-color: var(--black-1);
    color: var(--night-white);
}

select {
    cursor: pointer;
}

.field input:focus, .field select:focus {
    outline: 0;
    border-bottom: 1px solid #666;
    background-color: var(--night-yellow);
}

select {
    outline: none;
    background-color: rgba(0, 0, 0, 0);
}

select:active, select:focus {
    border-bottom: 1px solid #666;
}

.field label {
    text-transform: uppercase;
    letter-spacing: 0.05em;
    transition: 0.2s !important;
    color: var(--night-white);
}

/**
  * Translate down and scale the label up to cover the placeholder,
  * when following an input (with placeholder-shown support).
  * Also make sure the label is only on one row, at max 2/3rds of the
  * field—to make sure it scales properly and doesn't wrap.
  */

.field input:placeholder-shown+label {
    font-size: 0.7em;
    cursor: text;
    max-width: 66.66%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    transform-origin: left bottom;
    transform: translate(0, 1.4rem) scale(1.5);
    position: relative;
    bottom: 5px;
}

label {
    font-size: 0.7em !important;
}

/**
  * By default, the placeholder should be transparent. Also, it should 
  * inherit the transition.
  */

.field ::-webkit-input-placeholder {
    opacity: 0;
    transition: inherit;
}

.productSearchSearch ::-webkit-input-placeholder {
    color: black;
    transition: inherit;
}

.productSearchSearch input:focus::-webkit-input-placeholder {
    color: gray !important;
}

/**
  * Show the placeholder when the input is focused.
  */

input:focus::-webkit-input-placeholder, textarea:focus::-webkit-input-placeholder {
    opacity: 1;
    transition: 0.2s ease-in-out;
}

/**
  * When the element is focused, remove the label transform.
  * Also, do this when the placeholder is _not_ shown, i.e. when 
  * there's something in the input at all.
  */

.field input:not(:placeholder-shown)+label, .field input:focus+label, .field select+label {
    transform: translate(0, 0) scale(1);
    color: #8C8C8C;
}

.field input:focus+label {
    position: relative;
    bottom: 2px;
}

.hiddenIcon {
    opacity: 0;
}

.hiddenIcon:hover {
    opacity: 1;
    transition: 0.4s ease-in-out;
}

.visible:hover {
    opacity: 0;
}

.actionTableData {
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    min-width: calc(100% + 8px);
    margin-left: -4px;
}

.actionIconsContainer {
    position: relative;
    width: 10px;
    height: 10px;
}

.actionIconsContainer img {
    position: absolute;
    top: -5px;
    left: -5px;
}

/* .gridAction {
    position: relative;
    width: 50px;
    transition: 0.3s ease-in-out;
  } */

.actionTableData img {
    padding: 0;
    margin: 0;
    width: 20px;
    cursor: pointer;
}

/* .actionTableData .actionEdit {
    position: absolute;
    bottom: -11px;
    left: 1px;
  }
  .actionTableData .actionDelete {
    position: absolute;
    bottom: -11px;
    left: 30px;
  } */

.grid {
    position: relative;
    top: 4px;
    padding: 0 !important;
    width: 100%;
    overflow: scroll;
    border: 1px solid black;
    /* overflow-y: hidden; */
    border-radius: 5px;
    max-height: calc(100vh - 220px);
    /* border: 1px solid red; */
    background-color: var(--black-0);
}

.grid table tr th {
    position: relative;
    z-index: 100;
    outline: 1px solid black;
    border: none !important;
    cursor: pointer;
    /*   transition: 0.1s ease-in-out;
    transition-delay: 0.5s; */
}

.grid table tr th {
    -webkit-touch-callout: none;
    /* iOS Safari */
    -webkit-user-select: none;
    /* Safari */
    -khtml-user-select: none;
    /* Konqueror HTML */
    -moz-user-select: none;
    /* Old versions of Firefox */
    -ms-user-select: none;
    /* Internet Explorer/Edge */
    user-select: none;
    /* Non-prefixed version, currently */
}

.grid table tr:nth-child(2) th i {
    position: absolute;
    left: 5px;
    top: 11px;
    width: 20px;
    height: 20px;
    font-size: 15px;
    color: var(--night-white);
}

.grid table tr:nth-child(2) th input {
    background-color: var(--black-05);
    color: var(--night-white);
    border: none;
    outline: none;
    display: inline-block;
    min-height: 35px;
    font-size: 13px;
    min-width: calc(100%-30px);
    padding-left: 30px;
    width: 100%;
}

.grid table tr:nth-child(2) th {
    padding: 0 !important;
    margin: 0 !important;
    background-color: var(--black-1);
}

.grid table tr:nth-child(even) td {
    background-color: var(--black-05);
}

.grid table tr td:first-child, .grid table tr th:first-child {
    border-left: none !important;
}

.grid table tr td:last-child, .grid table tr th:last-child {
    border-right: none !important;
    padding: 10px;
}

.grid table {
    min-width: 100%;
    table-layout: auto;
    border-bottom: none !important;
}

.columnSelector:hover {
    border-right: 2px solid blue;
}

.searchResults {
    border: 1px solid var(--black-0);
    display: inline-block;
    width: calc(50% - 22px);
    background-color: var(--black-1);
    position: absolute;
    top: 39px;
