#grid-refresh {
    background: no-repeat center/18px url(./img/icons/refresh/refresh_nightwhite.png);
    background-color: var(--black-2);
    transition: 0.3s ease-in-out;
}

#grid-refresh:hover {
    background: no-repeat center/18px url(./img/icons/refresh/refresh_white.png);
    background-color: var(--black-3);
}

#grid-new {
    background: no-repeat center/18px url(./img/icons/new/new_nightwhite.png);
    background-color: var(--black-2);
    transition: 0.3s ease-in-out;
}

#grid-new:hover {
    background: no-repeat center/18px url(./img/icons/new/new_white.png);
    background-color: var(--black-3);
}

.grid input[type="checkbox"] {
    transform: scale(1.7);
    opacity: 0.8;
}

#settings {
    display: inline-flex;
    width: 35px;
    height: 35px;
    margin: 0 20px 0 8px;
    background: no-repeat center/22px url(./img/icons/settings/settings_nightwhite.png);
    border-radius: 50%;
    transition: 0.3s ease-in-out;
    cursor: pointer;
}

#settings:hover {
    background: no-repeat center/22px url(./img/icons/settings/settings_white.png);
}

/* The switch - the box around the slider */

.switch {
    position: relative;
    bottom: 2px;
    display: inline-block;
    width: 60px;
    height: 34px;
}

/* Hide default HTML checkbox */

.switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

/* The slider */

.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: var(--black-2);
    -webkit-transition: .2s;
    transition: .2s;
}

.slider:before {
    position: absolute;
    content: "";
    height: 30px;
    width: 30px;
    left: 2px;
    bottom: 2px;
    background-color: var(--black-3);
    transition: .2s;
}

input:checked+.slider {
    background-color: var(--night-blue2);
}

input:checked+.slider:after {
    font-family: sans-serif;
    content: 'ON';
    position: relative;
    left: 0;
    top: 0;
    color: var(--night-white);
    font-weight: 600;
    animation: fadeIn ease 1.4s;
    height: 34px;
    width: 43px;
    display: flex;
    justify-content: flex-end;
    padding: 0 9px;
    align-items: center;
    font-size: 12px;
}

input:checked+.slider:before {
    -webkit-transform: translateX(26px);
    -ms-transform: translateX(26px);
    transform: translateX(26px);
}

/* Rounded sliders */

.slider.round {
    border-radius: 34px;
}

.slider.round:before {
    border-radius: 50%;
}

